sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		// Create GlideDateTime objects for the current date and the passed in date
		// rightnow stores the current time
		var rightnow = new GlideDateTime();
		rightnow =  new GlideDateTime(rightnow.getDisplayValue());

		// Create a GlideDateTime object for the When needed date
		var testdate = new GlideDateTime(strDate);
		testdate = new GlideDateTime(testdate.getDisplayValue());

		var dur = new GlideDuration();
		dur = GlideDate.subtract(rightnow.getDate(), testdate.getDate());
		var dayDiff = dur.getDayPart();

		// Compare rightnow and testdate to see if they are the same day
		if (dayDiff == 0 ) {
			return true;
		}
		else {
			return false;
		}
},

    type: 'NeedItUtils'
};
